<?php

namespace Itkg\Tests;


use Itkg\Utils\SisterFinder;
use \Phake;

class SisterFinderTest extends \PHPUnit_Framework_TestCase
{


    private $compareUtils;
    private $sisterFinder;

    public function setUp()
    {
        $this->compareUtils = Phake::mock('Itkg\Utils\ImageCompareUtils');
        Phake::when($this->compareUtils)->getMaxDistance()->thenCallParent();
        Phake::when($this->compareUtils)->hammer(Phake::anyParameters())->thenCallParent();
        $this->sisterFinder = new SisterFinder($this->compareUtils);

    }

    public function testGenerateSisters()
    {
        $signatures = $this->getSignatures();
        $this->sisterFinder->generateSisters($signatures);
        $result = $this->sisterFinder->getSisters();
        Phake::verify($this->compareUtils,Phake::times(10))->getMaxDistance();
        Phake::verify($this->compareUtils,Phake::times(9))->hammer(Phake::anyParameters());
        $this->assertInternalType('array',$result);
        $this->assertCount(3, $result);

    }

    public function testSortResult()
    {
        $signatures = $this->getSignatures();
        $this->sisterFinder->generateSisters($signatures);
        $result = $this->sisterFinder->getSisters();
        $keys = array_keys($result);
        $this->assertEquals([8092, 8094, 8097], $keys);
        $this->sisterFinder->sortResult();
        $result = $this->sisterFinder->getSisters();
        $keys = array_keys($result);
        $this->assertEquals([8092, 8097, 8094], $keys);
    }

